schema:
{
    "$id": "http://disc.gsfc.nasa.gov/schemas/defWorkflow.json",
    "type": "object",
    "definitions": {},
    "$schema": "http://json-schema.org/draft-07/schema#",
    "title": "Workflow",
    "properties": {
        "name": {
            "$id": "/properties/name",
            "type": "string",
            "title": "Name",
            "examples": ["Edit-Review-Publish"]
        },
        "roles": {
            "$id": "/properties/roles",
            "type": "array",
            "title": "Roles",
            "minItems": 1,
            "items": {
                "$id": "/properties/roles/items",
                "type": "string",
                "examples": ["Author", "Reviewer", "Publisher"]
            }
        },
        "nodes": {
            "$id": "/properties/nodes",
            "type": "array",
            "title": "Nodes",
            "minItems": 1,
            "items": {
                "$id": "/properties/nodes/items",
                "type": "object",
                "properties": {
                    "id": {
                        "$id": "/properties/nodes/items/properties/role",
                        "type": "string",
                        "title": "ID",
                        "examples": ["Modified"]
                    },
                    "privileges": {
                        "type": "array",
                        "items": {
                            "type": "object",
                            "properties": {
                                "role": {
                                    "type": "string"
                                },
                                "privilege": {
                                    "type": "array",
                                    "items": {
                                        "type": "string"
                                    }
                                }
                            }
                        }
                    },
                    "readyForUse": {
                        "$id": "/properties/nodes/items/properties/readyForUse",
                        "type": "boolean",
                        "title": "Ready For Use Flag"
                    }
                }
            }
        },
        "edges": {
            "$id": "/properties/edges",
            "type": "array",
            "title": "Edges",
            "minItems": 1,
            "items": {
                "$id": "/properties/edges/items",
                "type": "object",
                "properties": {
                    "role": {
                        "$id": "/properties/edges/items/properties/role",
                        "type": "string",
                        "title": "Role",
                        "examples": ["Author"]
                    },
                    "source": {
                        "$id": "/properties/edges/items/properties/source",
                        "type": "string",
                        "title": "Source Node",
                        "examples": ["init"]
                    },
                    "target": {
                        "$id": "/properties/edges/items/properties/target",
                        "type": "string",
                        "title": "Target Node",
                        "examples": ["Modified"]
                    },
                    "label": {
                        "$id": "/properties/edges/items/properties/label",
                        "type": "string",
                        "title": "Label",
                        "default": "",
                        "examples": ["Create/Edit"]
                    },
                    "notify": {
                        "$id": "/properties/edges/items/properties/notify",
                        "type": "boolean",
                        "title": "Send notification",
                        "default": true
                    }
                }
            }
        }
    },
    "required": ["name", "roles", "nodes", "edges"]
}

{
    "$schema": "http://json-schema.org/schema#",
    "$id": "https://disc.gsfc.nasa.gov/schemas/model.json",
    "type": "object",
    "properties": {
        "id": {
            "title": "Earthdata Login ID",
            "description": "Earthdata Login ID",
            "type": "string",
            "minLength": 1,
            "examples": ["johndoe"]
        },
        "name": {
            "title": "Name of the user",
            "description": "Name of the user",
            "type": "string",
            "minLength": 1,
            "examples": ["John Doe"]
        },
        "roles": {
            "type": "array",
            "title": "Roles",
            "description": "User's roles",
            "items": {
                "type": "object",
                "properties": {
                    "model": {
                        "description": "Information model title",
                        "type": "string",
                        "title": "Model"
                    },
                    "role": {
                        "description": "User's role for the information model",
                        "type": "string",
                        "title": "Role"
                    }
                }
            }
        },
        "notes": {
            "description": "Notes about the user",
            "type": "string",
            "title": "Notes",
            "minLength": 1,
            "examples": ["Created in response to ticket #1234"]
        }
    },
    "required": ["id", "name"]
}

{
    "$schema": "http://json-schema.org/schema#",
    "$id": "https://disc.gsfc.nasa.gov/schemas/model.json",
    "type": "object",
    "required": ["title", "abstract", "type", "body"],
    "properties": {
        "title": {
            "type": "string",
            "description": "The title should detail any phenomena being explored, and the data and/or service being used."
        },
        "abstract": {
            "type": "string",
            "title": "Description",
            "description": "Describe the phenomena and data/service being used.",
            "minLength": 10,
            "maxLength": 500
        },
        "type": {
            "type": "string",
            "default": "News",
            "enum": ["News", "Featured Article", "Data Release"]
        },
    "body": {
            "type": "string",
            "contentMediaType": "text/html"
        },
        "additionalAuthors": {
            "type": "string"
        },
        "notes": {
            "type": "string",
            "title": "Revision Comments"
        }
    }
}
